/*
 * ClientWrapper: the socket-based client to issue requests and receive responses by multiple threads.
 *
 * Note that the requests are generated by an underlying workload (e.g., CacheBench).
 * The socket-based client just converts generated requests into network packets to test remote cache.
 * 
 * By Siyuan Sheng (2023.04.17).
 */

#ifndef CLIENT_WRAPPER_H
#define CLIENT_WRAPPER_H

#include <string>

#include "benchmark/client_param.h"

namespace covered
{
    class ClientWrapper
    {
    public:
        static void* launchClient(void* local_client_param_ptr);

        // The UDP ports used by Client is [local_client_workload_startport, local_client_workload_startport + client_thread_cnt - 1]
        ClientWrapper(ClientParam* local_client_param_ptr);
        ~ClientWrapper();

        void start();
    private:
        static const std::string kClassName;

        ClientParam* local_client_param_ptr_;
    };
}

#endif