/*
 * Worker: a worker thread launched by client to issue requests and receive responses.
 *
 * Note that the requests are generated by an underlying workload (e.g., CacheBench).
 * The worker will convert generated requests into network packets to test remote cache.
 * 
 * By Siyuan Sheng (2023.04.20).
 */

#ifndef WORKER_H
#define WORKER_H

#include <random> // std::mt19937_64
#include <string>

#include "benchmark/worker_param.h"

namespace covered
{
    class Worker
    {
    public:
        static void* launchWorker(void* local_worker_param_ptr);

        Worker(WorkerParam* local_worker_param_ptr);
        ~Worker();

        void start();
    private:
        static const std::string kClassName;

        WorkerParam* local_worker_param_ptr_;
        std::mt19937_64* request_randgen_ptr_;
    };
}

#endif